import React, { useEffect, useRef, useState } from 'react';
import './TranscriptionPanel.css';

const TranscriptionPanel = ({
  transcription,
  setTranscription,
  isTranscribing,
  onStartTranscription,
  onStopTranscription,
  onClearTranscription,
  transcriptionHistory,
  currentTime,
  videoDuration,
  formatTime
}) => {
  const [recognition, setRecognition] = useState(null);
  const [isSupported, setIsSupported] = useState(false);
  const [error, setError] = useState('');
  const textareaRef = useRef(null);

  useEffect(() => {
    // Check if Web Speech API is supported
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognitionInstance = new SpeechRecognition();
      
      recognitionInstance.continuous = true;
      recognitionInstance.interimResults = true;
      recognitionInstance.lang = 'en-US';

      recognitionInstance.onstart = () => {
        console.log('Speech recognition started');
      };

      recognitionInstance.onresult = (event) => {
        let finalTranscript = '';
        let interimTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }

        if (finalTranscript) {
          setTranscription(prev => prev + ' ' + finalTranscript);
        }
      };

      recognitionInstance.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setError(`Speech recognition error: ${event.error}`);
        onStopTranscription();
      };

      recognitionInstance.onend = () => {
        console.log('Speech recognition ended');
        if (isTranscribing) {
          // Restart if still transcribing
          recognitionInstance.start();
        }
      };

      setRecognition(recognitionInstance);
      setIsSupported(true);
    } else {
      setIsSupported(false);
      setError('Speech recognition is not supported in this browser. Please use Chrome or Edge.');
    }
  }, [isTranscribing, setTranscription, onStopTranscription]);

  useEffect(() => {
    if (isTranscribing && recognition && isSupported) {
      try {
        recognition.start();
      } catch (error) {
        console.error('Error starting recognition:', error);
        setError('Failed to start speech recognition');
      }
    } else if (!isTranscribing && recognition) {
      try {
        recognition.stop();
      } catch (error) {
        console.error('Error stopping recognition:', error);
      }
    }
  }, [isTranscribing, recognition, isSupported]);

  const handleStartTranscription = () => {
    setError('');
    onStartTranscription();
  };

  const handleStopTranscription = () => {
    onStopTranscription();
  };

  const handleClearTranscription = () => {
    onClearTranscription();
  };

  const copyToClipboard = () => {
    if (transcription.trim()) {
      navigator.clipboard.writeText(transcription).then(() => {
        // Show a brief success message
        const button = document.querySelector('.copy-btn');
        if (button) {
          const originalText = button.textContent;
          button.textContent = 'Copied!';
          setTimeout(() => {
            button.textContent = originalText;
          }, 2000);
        }
      }).catch(err => {
        console.error('Failed to copy text: ', err);
      });
    }
  };

  const downloadTranscription = () => {
    if (transcription.trim()) {
      const blob = new Blob([transcription], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `transcription-${new Date().toISOString().slice(0, 19).replace(/:/g, '-')}.txt`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  };

  return (
    <div className="transcription-panel">
      <div className="panel-header">
        <h3>üé§ Transcription</h3>
        <div className="transcription-controls">
          {!isTranscribing ? (
            <button
              className="start-btn"
              onClick={handleStartTranscription}
              disabled={!isSupported}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <circle cx="12" cy="12" r="10"/>
                <circle cx="12" cy="12" r="3"/>
              </svg>
              Start
            </button>
          ) : (
            <button
              className="stop-btn"
              onClick={handleStopTranscription}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <rect x="6" y="6" width="12" height="12"/>
              </svg>
              Stop
            </button>
          )}
          
          <button
            className="clear-btn"
            onClick={handleClearTranscription}
            disabled={!transcription.trim()}
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
              <path d="M3 6h18"/>
              <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6"/>
              <path d="M8 6V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"/>
            </svg>
            Clear
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
            <circle cx="12" cy="12" r="10"/>
            <line x1="15" y1="9" x2="9" y2="15"/>
            <line x1="9" y1="9" x2="15" y2="15"/>
          </svg>
          {error}
        </div>
      )}

      {!isSupported && (
        <div className="warning-message">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
            <path d="M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z"/>
            <line x1="12" y1="9" x2="12" y2="13"/>
            <line x1="12" y1="17" x2="12.01" y2="17"/>
          </svg>
          Speech recognition requires Chrome or Edge browser
        </div>
      )}

      <div className="transcription-content">
        <div className="transcription-textarea">
          <textarea
            ref={textareaRef}
            value={transcription}
            onChange={(e) => setTranscription(e.target.value)}
            placeholder={isTranscribing ? "Listening..." : "Transcription will appear here..."}
            readOnly={isTranscribing}
            className={isTranscribing ? 'listening' : ''}
          />
        </div>

        {transcription.trim() && (
          <div className="transcription-actions">
            <button className="copy-btn" onClick={copyToClipboard}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <rect x="9" y="9" width="13" height="13" rx="2" ry="2"/>
                <path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"/>
              </svg>
              Copy
            </button>
            <button className="download-btn" onClick={downloadTranscription}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
                <polyline points="7,10 12,15 17,10"/>
                <line x1="12" y1="15" x2="12" y2="3"/>
              </svg>
              Download
            </button>
          </div>
        )}
      </div>

      {transcriptionHistory.length > 0 && (
        <div className="transcription-history">
          <h4>üìù History</h4>
          <div className="history-list">
            {transcriptionHistory.map((item) => (
              <div key={item.id} className="history-item">
                <div className="history-header">
                  <span className="history-time">{item.timestamp}</span>
                  <span className="video-time">{formatTime(item.videoTime)}</span>
                </div>
                <div className="history-text">{item.text}</div>
              </div>
            ))}
          </div>
        </div>
      )}

      {videoDuration > 0 && (
        <div className="video-progress">
          <div className="progress-info">
            <span>Current: {formatTime(currentTime)}</span>
            <span>Duration: {formatTime(videoDuration)}</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill"
              style={{ width: `${(currentTime / videoDuration) * 100}%` }}
            ></div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TranscriptionPanel;
