import React, { useState } from 'react';
import './App.css';
import VideoUpload from './components/VideoUpload';
import TranscriptionPanel from './components/TranscriptionPanel';
import VideoPlayer from './components/VideoPlayer';

function App() {
  const [uploadedVideo, setUploadedVideo] = useState(null);
  const [transcription, setTranscription] = useState('');
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [transcriptionHistory, setTranscriptionHistory] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(0);

  const handleVideoUpload = (videoFile) => {
    setUploadedVideo(videoFile);
    setTranscription('');
    setCurrentTime(0);
    setVideoDuration(0);
  };

  const handleTimeUpdate = (currentTime, duration) => {
    setCurrentTime(currentTime);
    setVideoDuration(duration);
  };

  const startTranscription = () => {
    setIsTranscribing(true);
    setTranscription('');
  };

  const stopTranscription = () => {
    setIsTranscribing(false);
    if (transcription.trim()) {
      setTranscriptionHistory(prev => [...prev, {
        id: Date.now(),
        text: transcription,
        timestamp: new Date().toLocaleString(),
        videoTime: currentTime
      }]);
    }
  };

  const clearTranscription = () => {
    setTranscription('');
    setTranscriptionHistory([]);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸŽ¬ Video Transcription App</h1>
        <p>Upload a video and transcribe the audio to text</p>
      </header>

      <main className="App-main">
        <div className="app-container">
          <div className="left-panel">
            <VideoUpload onVideoUpload={handleVideoUpload} />
            {uploadedVideo && (
              <VideoPlayer 
                video={uploadedVideo}
                onTimeUpdate={handleTimeUpdate}
                isTranscribing={isTranscribing}
              />
            )}
          </div>

          <div className="right-panel">
            <TranscriptionPanel
              transcription={transcription}
              setTranscription={setTranscription}
              isTranscribing={isTranscribing}
              onStartTranscription={startTranscription}
              onStopTranscription={stopTranscription}
              onClearTranscription={clearTranscription}
              transcriptionHistory={transcriptionHistory}
              currentTime={currentTime}
              videoDuration={videoDuration}
              formatTime={formatTime}
            />
          </div>
        </div>
      </main>

      <footer className="App-footer">
        <p>Built with React and Web Speech API</p>
      </footer>
    </div>
  );
}

export default App;
